<?php

header('Content-Type: text/html; charset=UTF-8');

function rewrite(){
  global $hitokoto;
  $data = file_get_contents(__FILE__);
  $pos = strrpos($data, '$hitokoto');
  return file_put_contents(__FILE__, substr($data, 0, $pos+12).var_export($hitokoto, true).";\r\n\r\n\r\nhitokoto();\r\n");
}

function collect(){
  global $hitokoto;
  $addon = true ? '' : '?cat=g';
  $data = file_get_contents('http://api.hitokoto.us/rand'.$addon);
  if(!empty($data) && !in_array($data, $hitokoto)){
    $hitokoto[] = $data;
    rewrite();
  }
  highlight_file(__FILE__);
}

function resort(){
  global $hitokoto;
  foreach($hitokoto as $k => $sentence){
    $position = strrpos($sentence, '"id":') + 5;
    $id = substr($sentence, $position);
    if(strlen($id) > 11) continue;
    $hitokoto[$k] = substr($sentence, 0, strlen($sentence)-1).'000}';
  }
  rewrite();
  highlight_file(__FILE__);
}

function unique(){
  global $hitokoto;
  $new = array();
  foreach($hitokoto as $sentence){
    $position = strrpos($sentence, '"id":') + 5;
    $id = substr($sentence, $position);
    $new[$id] = $sentence;
  }
  $hitokoto = array_values($new);
  rewrite();
  highlight_file(__FILE__);
}

function hitokoto(){
  global $hitokoto;

  if($_GET['collect']){
    return collect();
  }elseif($_GET['resort']){
    return resort();
  }elseif($_GET['unique']){
    return unique();
  }

  $result = &$hitokoto[mt_rand(0, count($hitokoto)-1)];

  exit($_GET['fun'] ? "{$_GET['fun']}({$result})" : $result);
}

